CREATE TABLE IF NOT EXISTS `blogs_articles`  (
  `id` int UNSIGNED NOT NULL AUTO_INCREMENT COMMENT 'ID',
  `classifys_id` int NULL DEFAULT 0 COMMENT '所属分类',
  `name` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '名称',
  `image` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '图片',
  `content` text CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NOT NULL COMMENT '内容',
  `views` int NULL DEFAULT 0 COMMENT '浏览量',
  `memo` varchar(255) CHARACTER SET utf8mb4 COLLATE utf8mb4_general_ci NULL DEFAULT NULL COMMENT '备注',
  `weight` int NULL DEFAULT 0 COMMENT '排序',
  `created_at` datetime NULL DEFAULT NULL COMMENT '创建时间',
  `updated_at` datetime NULL DEFAULT NULL COMMENT '更新时间',
  `status` int NULL DEFAULT 0 COMMENT '禁用',
  PRIMARY KEY (`id`) USING BTREE
) ENGINE = InnoDB AUTO_INCREMENT = 21 CHARACTER SET = utf8mb4 COLLATE = utf8mb4_general_ci COMMENT = '文章管理' ROW_FORMAT = Dynamic;

LOCK TABLES `blogs_articles` WRITE;
INSERT INTO `blogs_articles` VALUES (1, 1, '英特尔在中国：开源之路上的20年携手共进', 'https://i.niupic.com/images/2023/06/06/b8to.jpg', '<p>英特尔与中国开源技术的故事也发生在 20 多年前。20 世纪 90 年代起，我国电信行业开始快速发展，英特尔从那时候就开始积极参与中国的开源社区。其中，2001 年在中国设立开源技术中心，是英特尔在中国开源道路上的一个重要开端。</p>\n<p>&nbsp;</p>\n<p>谈到英特尔中国开源技术中心，就不得不谈到电信级 Linux。随着 Linux 在电信领域的应用逐渐普及，英特尔也意识到市场对于电信级 Linux 的需求也越来越大，以及开源技术对于行业推动的重要性。</p>\n<p>&nbsp;</p>\n<p>因此英特尔在中国设立开源技术中心之后，带来的第一个开源项目就是电信级Linux CGL（Carrier Grade Linux）。CGL是由开放源代码发展实验室 (OSDL：Open Source Development Lab) 发起的、专门针对电信级服务的 Linux。CGL 在标准 Linux 的基础上,增加了一组为适应电信运营环境而设计的特性，承诺遵守 LSB 标准，保持 Linux 版本的一致性，并遵守开放源码的许可协议和开放源码的开发流程。CGL 成为电信行业的一个标准，一直沿用至今。</p>\n<p>&nbsp;</p>\n<p>更重要的是，英特尔希望以此为基础为中国的开源社区提供技术支持和服务，共同推动开源技术的进步和发展。这不仅表明了其对开源技术的支持和认可，同时也为中国的开源社区提供了更多的资源和机会，以及为英特尔在中国的发展和合作奠定了坚实的基础。</p>\n<p>&nbsp;</p>\n<p>此后的 20 年间，英特尔不断积极参与各种开源贡献，并在各大开源社区中扮演中重要角色。开源项目也逐步涵盖 Linux 内核、虚拟化、安卓、编译、云原生、网络、存储、大数据、AI、Web 技术等诸多领域。今年 2 月，英特尔再次对开源领域&ldquo;加码&rdquo;，正式成立英特尔中国开源技术委员会，委员会的英特尔开源软件专家、产品技术负责人和社区运营专家，将最大程度整合内部资源，利用英特尔最新技术和生态资源，更加深入地与中国的开放生态融合，更专业高效地服务中国开发者，推动开源生态建设和产业发展。</p>\n<p>&nbsp;</p>', 1026, '', 0, '2023-06-06 23:30:32', '2023-06-08 22:35:58', 0);
INSERT INTO `blogs_articles` VALUES (2, 4, 'Vue3侦听器watch的实现原理是什么', 'https://i.niupic.com/images/2023/06/08/b8DD.png', '<h3 style=\"margin: 30px 0px 12px; padding: 0px; box-sizing: border-box; font-weight: normal; color: #2d3037; position: relative; font-size: 20px; line-height: 1.4; font-family: \'Helvetica Neue\', Helvetica, Arial, \'Microsoft Yahei\', \'Hiragino Sans GB\', \'Heiti SC\', \'WenQuanYi Micro Hei\', sans-serif; background-color: #ffffff;\">watch 的本质</h3>\n<p style=\"margin: 24px 0px; padding: 0px; box-sizing: border-box; line-height: 30px; font-size: 16px; color: #2d3037; word-break: break-all; font-family: \'Helvetica Neue\', Helvetica, Arial, \'Microsoft Yahei\', \'Hiragino Sans GB\', \'Heiti SC\', \'WenQuanYi Micro Hei\', sans-serif; background-color: #ffffff;\">所谓的watch，其<strong style=\"margin: 0px; padding: 0px; box-sizing: border-box;\">本质</strong>就是观测一个响应式数据，当数据发生变化时通知并执行相应的回调函数。实际上，<strong style=\"margin: 0px; padding: 0px; box-sizing: border-box;\">watch 的实现本质</strong>就是利用了 effect 和 options.scheduler 选项。</p>\n<p style=\"margin: 24px 0px; padding: 0px; box-sizing: border-box; line-height: 30px; font-size: 16px; color: #2d3037; word-break: break-all; font-family: \'Helvetica Neue\', Helvetica, Arial, \'Microsoft Yahei\', \'Hiragino Sans GB\', \'Heiti SC\', \'WenQuanYi Micro Hei\', sans-serif; background-color: #ffffff;\">&nbsp;</p>\n<p style=\"margin: 24px 0px; padding: 0px; box-sizing: border-box; line-height: 30px; font-size: 16px; color: #2d3037; word-break: break-all; font-family: \'Helvetica Neue\', Helvetica, Arial, \'Microsoft Yahei\', \'Hiragino Sans GB\', \'Heiti SC\', \'WenQuanYi Micro Hei\', sans-serif; background-color: #ffffff;\">source 是响应式数据，cb 是回调函数。如果副作用函数中存在 scheduler 选项，当响应式数据发生变化时，会触发 scheduler 函数执行，而不是直接触发副作用函数执行。从这个角度来看， scheduler 调度函数就相当于是一个回调函数，而 watch 的实现就是利用了这点。</p>', 371, '', 0, '2023-06-07 18:17:48', '2023-06-09 11:38:58', 0);
INSERT INTO `blogs_articles` VALUES (3, 4, 'Vue3中的插件如何使用', 'https://i.niupic.com/images/2023/06/08/b8DD.png', '<h3 style=\"margin: 0px; padding: 0px; font-weight: normal; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 14px; font-family: 微软雅黑; background-color: #ffffff;\" data-id=\"heading-1\">一、插件概述</h3>\n<h4 style=\"margin: 0px; padding: 0px; font-weight: normal; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; font-family: 微软雅黑; background-color: #ffffff;\" data-id=\"heading-2\">1.1 插件简介</h4>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">插件是 Vue.js 中的一个重要组成部分，它可以让 Vue.js 更加灵活和高效。Vue.js插件可以增强Vue.js的功能性，使其更加强大且易于使用。</p>\n<h4 style=\"margin: 0px; padding: 0px; font-weight: normal; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; font-family: 微软雅黑; background-color: #ffffff;\" data-id=\"heading-3\">1.2 插件的使用</h4>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">在 Vue.js 应用程序中使用插件非常容易，只需简单地调用该插件即可。在Vue.js应用程序的初始设置中，通常需要注册插件。</p>\n<h4 style=\"margin: 0px; padding: 0px; font-weight: normal; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; font-family: 微软雅黑; background-color: #ffffff;\" data-id=\"heading-4\">1.3 插件的分类</h4>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">Vue.js 插件可以按照不同的分类方式进行划分，例如可以按照插件的功能进行分类，也可以按照插件的实现方式进行分类。</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">常用的 Vue.js 插件分类如下:</p>\n<ul class=\" list-paddingleft-2\" style=\"margin: 0px; padding: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); list-style: none; font-family: 微软雅黑; font-size: 14px; background-color: #ffffff;\">\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">路由插件</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">状态管理插件</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">构建工具插件</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">测试插件</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">插件聚合插件</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">模板编译插件</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">组件状态管理插件</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">响应式系统插件</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">插件注册插件</p>\n</li>\n</ul>', 668, '', 0, '2023-06-07 18:26:29', '2023-06-09 11:39:02', 0);
INSERT INTO `blogs_articles` VALUES (4, 4, '如何从零开始搭建vue3项目', 'https://i.niupic.com/images/2023/06/08/b8DD.png', '<h4 style=\"margin: 0px; padding: 0px; font-weight: normal; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; font-family: 微软雅黑; background-color: #ffffff;\"><strong style=\"margin: 0px; padding: 0px;\">1.</strong>&nbsp;使用 vscode 开发工具安装vue3的插件&nbsp;<strong style=\"margin: 0px; padding: 0px;\">Volar</strong>&nbsp;，在vue2中我们使用的是Vetur。</h4>\n<ul class=\" list-paddingleft-2\" style=\"margin: 0px; padding: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); list-style: none; font-family: 微软雅黑; font-size: 14px; background-color: #ffffff;\">\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">vue3在线code工具 传送门sfc.vuejs.org/</p>\n</li>\n</ul>\n<h4 style=\"margin: 0px; padding: 0px; font-weight: normal; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; font-family: 微软雅黑; background-color: #ffffff;\"><strong style=\"margin: 0px; padding: 0px;\">2.</strong>&nbsp;执行初始化及安装命令：</h4>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\"><code style=\"margin: 0px; padding: 2px 4px; font-size: 14.4px; color: #c7254e; background-color: #f9f2f4; border-radius: 4px;\">npm init vite</code>&nbsp;初始化vite此过程可以输入项目名、选择vue/react项目及js/ts环境选择，vue3已经完全支持ts，此文章使用的是js。<code style=\"margin: 0px; padding: 2px 4px; font-size: 14.4px; color: #c7254e; background-color: #f9f2f4; border-radius: 4px;\">npm install</code>&nbsp;安装依赖。最后执行<code style=\"margin: 0px; padding: 2px 4px; font-size: 14.4px; color: #c7254e; background-color: #f9f2f4; border-radius: 4px;\">npm run dev</code>运行项目。</p>', 699, '', 0, '2023-06-07 18:28:12', '2023-06-08 14:11:25', 0);
INSERT INTO `blogs_articles` VALUES (5, 3, 'thinkphp如何清除日志', 'https://i.niupic.com/images/2023/06/08/b8DE.png', '<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\"><strong style=\"margin: 0px; padding: 0px;\">一、了解ThinkPHP日志</strong></p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">在使用ThinkPHP过程中，会产生以下两种类型的日志：</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">1.系统日志：存放在<code style=\"margin: 0px; padding: 2px 4px; font-size: 14.4px; color: #c7254e; background-color: #f9f2f4; border-radius: 4px;\">runtime</code>目录下的<code style=\"margin: 0px; padding: 2px 4px; font-size: 14.4px; color: #c7254e; background-color: #f9f2f4; border-radius: 4px;\">log</code>文件夹里面，包括请求日志、异常日志等。</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">2.SQL日志：存放在<code style=\"margin: 0px; padding: 2px 4px; font-size: 14.4px; color: #c7254e; background-color: #f9f2f4; border-radius: 4px;\">runtime</code>目录下的<code style=\"margin: 0px; padding: 2px 4px; font-size: 14.4px; color: #c7254e; background-color: #f9f2f4; border-radius: 4px;\">sql</code>文件夹里面，记录了SQL执行的详细信息。</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\"><strong style=\"margin: 0px; padding: 0px;\">二、清除ThinkPHP日志</strong></p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">清除ThinkPHP日志可以通过以下两种方式进行：</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">1.手动清除日志</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">手动清除日志比较简单易懂，只需要找到<code style=\"margin: 0px; padding: 2px 4px; font-size: 14.4px; color: #c7254e; background-color: #f9f2f4; border-radius: 4px;\">runtime</code>目录下的<code style=\"margin: 0px; padding: 2px 4px; font-size: 14.4px; color: #c7254e; background-color: #f9f2f4; border-radius: 4px;\">log</code>和<code style=\"margin: 0px; padding: 2px 4px; font-size: 14.4px; color: #c7254e; background-color: #f9f2f4; border-radius: 4px;\">sql</code>文件夹，直接将文件夹内的所有文件删除即可。</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">2.设置定期清理</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">手动清理日志虽然简单，但是需要定期执行，否则日志文件依旧会占用一定的磁盘空间。因此，我们可以在ThinkPHP的配置文件<code style=\"margin: 0px; padding: 2px 4px; font-size: 14.4px; color: #c7254e; background-color: #f9f2f4; border-radius: 4px;\">config.php</code>中设置定期清理。</p>', 0, '', 0, '2023-06-07 18:29:50', '2023-06-07 18:29:50', 0);
INSERT INTO `blogs_articles` VALUES (6, 2, 'MySQL存储引擎怎么理解', 'https://s1.ax1x.com/2023/06/08/pCkaGt0.jpg', '<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\"><strong style=\"margin: 0px; padding: 0px;\">作用描述</strong>：</p>\n<ul class=\" list-paddingleft-2\" style=\"margin: 0px; padding: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); list-style: none; font-family: 微软雅黑; font-size: 14px; background-color: #ffffff;\">\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">Engine：引擎名称（描述）；</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">Support：当前版本数据库是否支持该存储引擎，YES：支持、NO：不支持；<strong style=\"margin: 0px; padding: 0px;\">Supports transactions, row-level locking, and foreign keys</strong>，<strong style=\"margin: 0px; padding: 0px;\">个人字面上翻译这段话：支持事务、行级别锁和外键</strong>；</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">Comment：对该存储引擎的详情描述，比如描述该引擎否支持事务和外键；</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">Transactions：对该存储引擎是否支持事务的描述，YES：支持、NO：不支持；</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">XA：是否满足XA规范。XA规范是开放群组关于分布式事务处理(DTP)的规范。YES：支持、NO：不支持；</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">Savepoints：字面意思是保存点，对事物控制是否支持，YES：支持、NO：不支持。</p>\n</li>\n</ul>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">小声哔哔，如果你能阅读明白官方的一些英文文档，这将有助于你对MySQL存储引擎的进一步理解，养成阅读源码或者文档的能力。</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">顺带的提一下MySQL的妹妹MariaDB。在MySQL的复刻版本MariaDB中10.2之前使用的自带的新引擎Aria，在MariaDB10.2之后使用的默认存储引擎也是InnoDB，足以看出InnoDB存储引擎的优秀之处。MariaDB的API和协议兼容MySQL，另外又添加了一些功能，以支持本地的非阻塞操作和进度报告。这意味着，所有使用MySQL的连接器、程序库和应用程序也将可以在MariaDB下工作。在此基础上，由于担心甲骨文MySQL的一个更加封闭的软件项目，Fedora等Linux发行版已经在最新版本中以MariaDB取代MySQL，维基媒体基金会的服务器同样也使用MariaDB取代了MySQL。</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\"><strong style=\"margin: 0px; padding: 0px;\">主要</strong>需要<strong style=\"margin: 0px; padding: 0px;\">了解的几种存储引擎</strong>：</p>\n<ul class=\" list-paddingleft-2\" style=\"margin: 0px; padding: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); list-style: none; font-family: 微软雅黑; font-size: 14px; background-color: #ffffff;\">\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\"><strong style=\"margin: 0px; padding: 0px;\">MyISAM</strong></p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\"><strong style=\"margin: 0px; padding: 0px;\">InnoDB</strong></p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">MEMORY</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">MERGE</p>\n</li>\n</ul>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">下面将着重介绍我最近看书认识的几种常用的存储引擎，对比各个存储引擎之间的区别，帮助我们理解不同存储引擎的使用方式。更多详情可以参考MySQL的官方文档。</p>', 0, '', 0, '2023-06-07 18:31:18', '2023-06-07 18:31:18', 0);
INSERT INTO `blogs_articles` VALUES (7, 2, 'MySQL的SQL优化、索引优化、锁机制、主从复制知识有哪些', 'https://s1.ax1x.com/2023/06/08/pCkaGt0.jpg', '<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\"><strong style=\"margin: 0px; padding: 0px;\">面试问MyISAM和INNODB的区别</strong>：</p>\n<ul class=\" list-paddingleft-2\" style=\"margin: 0px; padding: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); list-style: none; font-family: 微软雅黑; font-size: 14px; background-color: #ffffff;\">\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">1.事务安全</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">2.查询和添加速度</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">3.支持全文索引</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">4.锁机制</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">5.外键MyISAM不支持外键，INNODB 支持外键.</p>\n</li>\n</ul>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\"><strong style=\"margin: 0px; padding: 0px;\">Mermory存储</strong>：比如我们数据变化频繁，不需要入库，同时又频繁的查询和修改，我们考虑使用memory</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\"><strong style=\"margin: 0px; padding: 0px;\">查看mysql以提供什么存储引擎</strong>：<code style=\"margin: 0px; padding: 2px 4px; font-size: 14.4px; color: #c7254e; background-color: #f9f2f4; border-radius: 4px;\">show engines;</code></p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\"><strong style=\"margin: 0px; padding: 0px;\">查看mysql当前默认的存储引擎</strong>：<code style=\"margin: 0px; padding: 2px 4px; font-size: 14.4px; color: #c7254e; background-color: #f9f2f4; border-radius: 4px;\">show variables like \'%storage_engine%\';</code></p>\n<h3 style=\"margin: 0px; padding: 0px; font-weight: normal; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 14px; font-family: 微软雅黑; background-color: #ffffff;\">1 SQL性能分析</h3>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\"><strong style=\"margin: 0px; padding: 0px;\">SQL性能下降原因</strong>：</p>\n<ul class=\" list-paddingleft-2\" style=\"margin: 0px; padding: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); list-style: none; font-family: 微软雅黑; font-size: 14px; background-color: #ffffff;\">\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">1、查询语句写的烂</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">2、索引失效（数据变更）</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">3、关联查询太多join（设计缺陷或不得已的需求）</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">4、服务器调优及各个参数设置（缓冲、线程数等）</p>\n</li>\n</ul>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\"><strong style=\"margin: 0px; padding: 0px;\">通常SQL调优过程</strong>：</p>\n<ul class=\" list-paddingleft-2\" style=\"margin: 0px; padding: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); list-style: none; font-family: 微软雅黑; font-size: 14px; background-color: #ffffff;\">\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">观察，至少跑1天，看看生产的慢SQL情况。</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">开启慢查询日志，设置阙值，比如超过5秒钟的就是慢SQL，并将它抓取出来。</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">explain + 慢SQL分析。</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">show profile。</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">运维经理 or DBA，进行SQL数据库服务器的参数调优。</p>\n</li>\n</ul>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\"><strong style=\"margin: 0px; padding: 0px;\">总结</strong>：</p>\n<ul class=\" list-paddingleft-2\" style=\"margin: 0px; padding: 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); list-style: none; font-family: 微软雅黑; font-size: 14px; background-color: #ffffff;\">\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">1、慢查询的开启并捕获</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">2、explain + 慢SQL分析</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">3、show profile查询SQL在Mysql服务器里面的执行细节和生命周期情况</p>\n</li>\n<li style=\"margin: 8px 0px 8px 30px; padding: 0px; list-style: unset; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; color: #3d464d;\">\n<p style=\"margin: 15px 0px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); box-sizing: border-box; border-radius: 8px; padding: 0px !important 0px !important 0px !important 10px;\">4、SQL数据库服务器的参数调优</p>\n</li>\n</ul>', 0, '', 0, '2023-06-07 18:32:24', '2023-06-07 18:32:24', 0);
INSERT INTO `blogs_articles` VALUES (8, 2, 'Redis的RDB和AOF方法怎么用', 'https://i.niupic.com/images/2023/06/08/b8DC.jpg', '<h4 style=\"margin: 0px; padding: 0px; font-weight: normal; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; font-family: 微软雅黑; background-color: #ffffff;\">Redis持久化方案</h4>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">Redis是内存数据库，数据都是存储在内存中，为了避免进程退出导致数据的永久丢失，需要定期将Redis中的数据以某种形式（数据或命令）从内存保存到硬盘。当下次Redis重启时，利用持久化文件实现数据恢复。除此之外，为了进行灾难备份，可以将持久化文件拷贝到一个远程位置。</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">&nbsp;</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">Redis提供了多种不同级别的持久化方式：一种是RDB，另一种是AOF。</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">RDB持久化可以在指定的时间间隔内生成数据集的时间点快照（point-in-time snapshot），将数据库的快照（snapshot）以二进制的方式保存到磁盘中。</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">AOF持久化记录服务器执行的所有更改操作命令，AOF文件中的命令全部以Redis协议的格式来保存，新命令会被追加到文件的末尾。Redis还可以在后台对AOF文件进行重写（rewrite），使得AOF文件的体积不会超出保存数据集状态所需的实际大小。</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">Redis可以同时使用AOF持久化和RDB持久化。在这种情况下，当Redis重启时，它会优先使用AOF文件来还原数据集，因为AOF文件保存的数据集通常比RDB文件所保存的数据集更完整。你甚至可以关闭持久化功能，让数据只在服务器运行时存在。</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">了解RDB持久化和AOF持久化之间的异同是非常重要的，以下几个小节将详细地介绍这这两种持久化功能，并对它们的相同和不同之处进行说明。</p>\n<h4 style=\"margin: 0px; padding: 0px; font-weight: normal; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); font-size: 16px; font-family: 微软雅黑; background-color: #ffffff;\">RDB快照</h4>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">下面我们说一下Redis的第一个持久化策略，RDB快照。Redis支持将当前内存数据的快照存成一个数据文件的持久化机制，而一个持续写入的数据库如何生成快照呢？Redis巧妙地借助了fork命令的写时复制（copy on write）机制，将当前进程fork出一个子进程，子进程根据内存快照，循环将数据持久化为RDB文件。</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">在默认情况下， Redis将数据库快照保存在根目录下，名字为dump.rdb的二进制文件中。可通过参数dir配置指定保存目录，dbfilename指定文件名。你可以对Redis进行设置，如&ldquo;save N M&rdquo;，表示N秒内数据项中有M个改动时这一条件被满足时，自动保存一次数据集。比如你可以配置当10分钟以内有100次写入就生成快照，也可以配置当1分钟内有1000次写入就生成快照，支持可以多个规则一起生效，当匹配到哪个就哪个规则生效。这些规则的定义就在Redis的配置文件中，你也可以通过Redis的CONFIG SET命令在Redis运行时设置规则，不需要重启Redis。</p>', 0, '', 0, '2023-06-07 18:33:27', '2023-06-07 18:33:27', 0);
INSERT INTO `blogs_articles` VALUES (9, 2, 'mysql级联删除功能怎么使用', 'https://s1.ax1x.com/2023/06/08/pCkaGt0.jpg', '<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">一、MySQL 基础知识回顾</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">在进行MySQL级联删除操作之前，我们需要先了解一些MySQL的基础知识。</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">1.关系型数据库的特点</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">关系型数据库是指使用表格来组织数据的数据库。它的特点是：</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">（1）数据以表格形式存储，每个表格表示一种实体类型。</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">（2）表格中的列代表不同的属性，行则代表不同的实例。</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">（3）表格之间可以通过主键-外键的关联关系建立联系。</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">2.关键字</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">MySQL中有很多关键字，如：SELECT、FROM、WHERE、GROUP BY、HAVING、ORDER BY、JOIN等等。这些关键字都是用来执行不同的数据库操作的。</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">3.基本操作</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">MySQL有四种基本操作：SELECT、INSERT、UPDATE和DELETE。其中，DELETE用于删除数据库中的数据，也就是我们今天要讲的知识点&mdash;&mdash;级联删除。</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">二、MySQL 级联删除基础知识</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">在MySQL中，级联删除主要涉及到的就是外键约束的问题。外键在数据库中用于建立表之间的关联关系。为了维护数据的完整性和一致性，当存在外键约束时，需要先删除相关的从表记录，然后才能删除主表中的某个记录。</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">1.外键约束</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">在关系型数据库中，外键约束被用来维护表之间的关联。简而言之，就是通过外键来将两个或多个表格连接起来。</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">在MySQL中，外键约束主要分为两类：级联控制和限制控制。级联控制可以在执行操作时对外键所在的行实施级联操作，包括级联更新和级联删除。</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">2.级联删除</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">在MySQL中，使用外键约束时，可以通过设置级联删除规则来实现删除主表记录时自动删除从表记录。实际上就是一条语句&ldquo;ON DELETE CASCADE&rdquo;，它会删除所有关联从表中的记录，以保持数据的一致性。</p>', 1, '', 0, '2023-06-07 18:34:12', '2023-06-08 14:00:42', 0);
INSERT INTO `blogs_articles` VALUES (10, 3, 'php数组键值区分类型吗', 'https://i.niupic.com/images/2023/06/08/b8DE.png', '<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\"><strong style=\"margin: 0px; padding: 0px;\">PHP数组的键值不区分类型，因为在使用时PHP将自动强制转换键值或值的数据类型。</strong></p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">这意味着，如果您使用字符串键值，则可以将整数作为索引使用，并且如果您使用非字符串键值，则PHP将自动将其转换为字符串。</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">&nbsp;</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">整数索引被保留并作为整数输出，而字符串索引则被转换为字符串，并在输出中显示了引号。</p>\n<p style=\"margin: 15px 0px; padding: 0px 0px 0px 10px; -webkit-tap-highlight-color: rgba(0, 0, 0, 0); color: #3d464d; font-size: 16px; box-sizing: border-box; border-radius: 8px; font-family: 微软雅黑; background-color: #ffffff;\">要明确知道一个键值是什么类型，您可以使用is_int()、is_string()等函数来检查它们的数据类型。</p>', 0, '', 0, '2023-06-07 18:36:17', '2023-06-07 18:36:17', 0);
INSERT INTO `blogs_articles` VALUES (11, 3, '什么是php超全局变量数组', 'https://i.niupic.com/images/2023/06/08/b8DE.png', '<p><span style=\"color: #3d464d; font-family: 微软雅黑; font-size: 16px; background-color: #f5f5f5;\">PHP中超全局变量是一类预定义的全局变量数组，可在脚本中使用，无需开发者声明即可访问，这些数组包括$_GET，$_POST，$_REQUEST，$_COOKIE等。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"color: #3d464d; font-family: 微软雅黑; font-size: 16px; background-color: #ffffff;\">PHP超级全局变量数组（Super Global Array），又称为PHP预定义数组，是由PHP引擎内置的，不需要开发者重新定义。 在PHP脚本运行时，PHP会自动将一些数据放在超级全局数组中。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"color: #3d464d; font-family: 微软雅黑; font-size: 16px; background-color: #ffffff;\">1、$_GET 数组用于收集通过 GET 方法提交的表单数据，通常以 URL 参数形式出现</span></p>\n<p>&nbsp;</p>\n<p><span style=\"color: #3d464d; font-family: 微软雅黑; font-size: 16px; background-color: #ffffff;\">2、$_POST 数组用于收集通过 POST 方法提交的表单数据，可用于处理敏感数据</span></p>\n<p>&nbsp;</p>\n<p><span style=\"color: #3d464d; font-family: 微软雅黑; font-size: 16px; background-color: #ffffff;\">3、$_REQUEST 数组包含了GET、_POST、$_COOKIE 的内容。可以用来收集 HTML 表单提交后的数据或者从浏览器地址栏中获取数据。</span></p>\n<p>&nbsp;</p>\n<p><span style=\"color: #3d464d; font-family: 微软雅黑; font-size: 16px; background-color: #ffffff;\">4、$_COOKIE 数组用于访问已在客户端计算机上存储的 cookie</span></p>\n<p>&nbsp;</p>\n<p>&nbsp;</p>', 1, '', 0, '2023-06-07 18:38:19', '2023-06-08 14:02:47', 0);
INSERT INTO `blogs_articles` VALUES (12, 3, 'php中json数组如何求和', 'https://i.niupic.com/images/2023/06/08/b8DE.png', '<p><span style=\"color: #3d464d; font-family: 微软雅黑; font-size: 16px; background-color: #f5f5f5;\">php中json数组求和方法：</span></p>\n<p>&nbsp;</p>\n<p><span style=\"color: #3d464d; font-family: 微软雅黑; font-size: 16px; background-color: #f5f5f5;\">1、创建一个PHP示例文件；</span></p>\n<p>&nbsp;</p>\n<p><span style=\"color: #3d464d; font-family: 微软雅黑; font-size: 16px; background-color: #f5f5f5;\">2、定义变量$jsonArrayStr存储提供的JSON数组，并改为字符串类型；</span></p>\n<p>&nbsp;</p>\n<p><span style=\"color: #3d464d; font-family: 微软雅黑; font-size: 16px; background-color: #f5f5f5;\">3、使用&ldquo;json_decode()&rdquo;函数将其解码为PHP数组进行处理；</span></p>\n<p>&nbsp;</p>\n<p><span style=\"color: #3d464d; font-family: 微软雅黑; font-size: 16px; background-color: #f5f5f5;\">4、使用array_reduce()函数，迭代计算出的值并返回总结果；</span></p>\n<p>&nbsp;</p>\n<p><span style=\"color: #3d464d; font-family: 微软雅黑; font-size: 16px; background-color: #f5f5f5;\">5、echo输出打印结果即可。</span></p>', 0, '', 0, '2023-06-07 18:40:39', '2023-06-07 18:40:39', 0);
